CREATE PROCEDURE [dbo].[CalcHexInChar2Int]
AS
declare @CurStr as char(8)
declare @CurDgtStr as char(1)
declare @CurDgt as int
declare @Res as bigint
declare @MultParam as bigint
declare @Pos as int

set @CurStr='2000c8a1'
set @Pos=8
set @MultParam=1
set @Res=0
while (@Pos>0)
  begin
    set @CurDgtStr=SubString(@CurStr,@Pos,1)
    set @CurDgt= case @CurDgtStr
      when '0' then 0
      when '1' then 1
      when '2' then 2
      when '3' then 3
      when '4' then 4
      when '5' then 5
      when '6' then 6
      when '7' then 7
      when '8' then 8
      when '9' then 9
      when ('a') then 10
      when ('b') then 11
      when ('c') then 12
      when ('d') then 13
      when ('e') then 14
      when ('f') then 15
      when ('A') then 10
      when ('B') then 11
      when ('C') then 12
      when ('D') then 13
      when ('E') then 14
      when ('F') then 15
    end
    set @Res=@Res+(@CurDgt*@MultParam)
    set @MultParam=@MultParam*16
    set @Pos=@Pos-1
  end
GO
