CREATE FUNCTION [dbo].[BitTest](@BinaryExpression int, @BitNumber int)
RETURNS bit AS  
BEGIN 
RETURN CASE WHEN (@BinaryExpression & POWER(2,@BitNumber)) != 0 THEN 1 ELSE 0 END
END
