CREATE FUNCTION [dbo].[Dec2Bit]( @d int )
returns varchar(255)
as
begin
  declare @b varchar(255)
  set @b = ''
  while ( @d > 0 )
  begin
    set @b =  cast( @d % 2 as varchar ) + @b
    set @d = round( @d / 2, 0 )
  end
  return ( replicate( '0', 32-len(@b) ) + @b )
END
GO
