CREATE   view [dbo].[M_ReadableTransactions] as
select ДатаРасч, Накл as [Номер накладной],
	ПлатНом as [Номер документа],
	Скл, ОпТип, Возврат, ПлатРежим, Клн,
	CASE isnull(ОпТип,0) + isnull(Возврат,0)*10 + isnull(ПлатРежим,0)*100
		WHEN 1 THEN 'П'
		WHEN 2 THEN 'Р'
		WHEN 3 THEN 'С'
		WHEN 11 THEN 'ВП'
		WHEN 12 THEN 'ВР'
		WHEN 100 THEN 'ПБ'
		WHEN 200 THEN 'ПК'
		WHEN 300 THEN 'РБ'
		WHEN 400 THEN 'РК'
		ELSE 'Тип не определен'
	END AS Операция,	
	case КлнТип
	-- только если клиент
		when 2 then (select КлнНаим from KAT_KLN where kat_kln.КлнКод = m_trans.Клн)
--		when 2 'Клиент'
		else 'Не определен клиент'
	end as [Клиент],
-- колонка прихода
case (isnull(ОпТип,0) + isnull(Возврат,0)*10 + isnull(ПлатРежим,0)*100)
	-- если приход
	when 1 then Сумма
	-- приход-возврат
	when 11 then Сумма
	-- приход по банку
	when 100 then Сумма
	-- приход по кассе
	when 200 then Сумма
	else 0
	end as [Приход],
-- колонка расхода
case (isnull(ОпТип,0) + isnull(Возврат,0)*10 + isnull(ПлатРежим,0)*100)
	-- расход
	when 2 then Сумма
	-- расход-возврат
	when 12 then Сумма
	-- расход по банку
	when 300 then Сумма
	-- расход по кассе
	when 400 then Сумма
	else 0
	end as [Расход],
-- колонка движения средств
case (isnull(ОпТип,0) + isnull(Возврат,0)*10 + isnull(ПлатРежим,0)*100)
	-- если приход
	when 1 then Сумма
	-- приход-возврат
	when 11 then Сумма
	-- приход по банку
	when 100 then Сумма
	-- приход по кассе
	when 200 then Сумма
	-- расход
	when 2 then -Сумма
	-- расход-возврат
	when 12 then -Сумма
	-- расход по банку
	when 300 then -Сумма
	-- расход по кассе
	when 400 then -Сумма
	else 0
	end as Платеж,
	Сальдо
-- comment
from m_trans
where КлнТип=2
