-- Author:        ЕФ
-- Create date: 26,10,2011
-- Description:    Функция вовращает акцию для товара
-- =============================================
CREATE FUNCTION [dbo].[AKC] 
(    
    -- Add the parameters for the function here
    @КлнКод int,
    @ТовГрп int,
	@ТовВид int,
    @ТовКод int
	
)
RETURNS TABLE 
AS
RETURN 
(
	select top 1 *
 from (
        select al.*,
               at . Тип GVT,
               at . ПроцентЦена
        from akc_list al (nolock) 
             inner join akc_kln ak (nolock) on ak.АкцияКод = al.АкцияКод
             inner join akc_tvr at (nolock) on at . АкцияКод = al.АкцияКод
        where ak.Тип = 0 and
              al.Статус = 1 AND
              ak.КлнТТ = @КлнКод AND
              at . Тип = 0 AND
              at . Товар = @ТовГрп AND
              GetDate() between al.РДатаНачала and al.РДатаКонца
        UNION ALL
        select al.*,
               at . Тип,
               at . ПроцентЦена
        from akc_list al (nolock) 
             inner join akc_kln ak (nolock) on ak.АкцияКод = al.АкцияКод
             inner join akc_tvr at (nolock) on at . АкцияКод = al.АкцияКод
        where ak.Тип = 0 and
              al.Статус = 1 AND
              ak.КлнТТ = @КлнКод AND
              at . Тип = 1 AND
              at . Товар = @ТовВид AND
              GetDate() between al.РДатаНачала and al.РДатаКонца
        UNION ALL
        select al.*,
               at . Тип,
               at . ПроцентЦена
        from akc_list al (nolock) 
             inner join akc_kln ak (nolock) on ak.АкцияКод = al.АкцияКод
             inner join akc_tvr at (nolock) on at . АкцияКод = al.АкцияКод
        where ak.Тип = 0 and
              al.Статус = 1 AND
              ak.КлнТТ = @КлнКод AND
              at . Тип = 2 AND
              at . Товар = @ТовКод AND
              GetDate() between al.РДатаНачала and al.РДатаКонца
      ) tt
 order by GVT desc,
          РДатаСоздания desc)
