CREATE VIEW [dbo].[VIEW2]
AS
SELECT     dbo.F_OPER.ОпКодТов, dbo.D_NAKL.НаклКлн AS ОпКодКлн, dbo.D_NAKL.НаклСклКод, dbo.D_NAKL.НаклДата AS ОпДата, 
