                      SUM(dbo.F_OPER.ОпКол * (dbo.F_OPER.ОпТип - ABS(dbo.F_OPER.ОпТип / 2) * 2)) AS КолПрих, 
                      SUM(dbo.F_OPER.ОпКол * (1 - (dbo.F_OPER.ОпТип - ABS(dbo.F_OPER.ОпТип / 2) * 2))) AS КолРасх, SUM(dbo.F_OPER.ОпСтоим) AS СумСтоим, 
