						from [Бонус].[dbo].Bonus as bn
							left join [Бонус].[dbo].Bonus_Tvr as btv on bn.idbon = btv.idbon and (btv.iskl = 0 or btv.iskl is NULL)
							left join [Бонус].[dbo].[Bon_type] as btp on bn.idBon_type = btp.id_type
