															when l.ОпВозврат=0 and l.ОпТип=1 and l.ОпКлнТип=2 --приход от поставщика
															then 1
															when l.ОпВозврат=1 and l.ОпТип=1 and l.ОпКлнТип=2 -- возврат от клиента
