CREATE FUNCTION [dbo].[UpdatedPrihcleanSum]
(
@date datetime
,@Kod bigint
,@kol float
)

/*

select * from dbo.[UpdatedPrihcleanSum]('20100203',1110,1000) order by ОпДатаРасч

