insert DOLG_MGR
SELECT     TOP 100 PERCENT dbo.KAT_PPL.СотрКод, dbo.D_ORG.СотрФамилияИмя, dbo.D_ORG.ГрпСотрНаим, ABS(SUM(dbo.DOLG.ДолгСумма)) 
