                      dbo.DOLG_SELMGR ON dbo.D_ORG.СотрФамилияИмя = dbo.DOLG_SELMGR.ВбрСотрНаим
WHERE ABS(ДолгСумма)>=@pSum and ABS(ДолгСрок)>=@pSrok
