	PR_Euras.dbo.D_TVR.ТовВидНаим as [Брэнд]	

FROM	PR_Euras.dbo.D_TVR INNER JOIN
            PR_Euras.dbo.F_RASH ON PR_Euras.dbo.D_TVR.ТовКод = PR_Euras.dbo.F_RASH.ОпКодТов INNER JOIN
