	@enddate date = NULL
AS
BEGIN
	-- SET NOCOUNT ON added to prevent extra result sets from
	-- interfering with SELECT statements.
	SET NOCOUNT ON;

	if(@enddate is NULL)
	begin
		set @enddate = DATEADD(DAY,-1,cast(GETDATE() as date))
	end
	
	if(@startdate is NULL)
	begin
