								FROM
								edi.FIRM_GLN o 
								WHERE gln = @ourfirmiln;				
								
								--ПЕРЕДАЧА ДОКУМЕНТА
								
								
								declare @rr varchar(max);
								
								select 
								@rr = dbo.Send(								
								@login,
								@password,
								@PartnerIln,
								'DELIVERYNOTE',
								'ECODRU1', 
								'XML', 
								'P', 
								'', 
								@ordrsp,
								5000
								);
								
								print @rr;
								--[edi].[CreateKorusResponseInvoice]	160
								UPDATE edi.KorusMessages
								set 
								delnot = 1,
								--Response = @ordrsp,
								DelnoteXML = @ordrsp
								WHERE iddoc = @oedinum
								
																--select @ordrsp,@login,@password
								    
		END;
    
        
    FETCH NEXT FROM oedi_cursor 
INTO @oedinum
END 
