                      ltrim(КлнАдрес) = '' AND ltrim(a.КлнНаим) <> '' THEN a.КлнНаим WHEN ltrim(КлнГрузАдрес) = '' AND ltrim(КлнАдрес) = '' AND ltrim(a.КлнНаим) 
