			when КлнНаВид<>0 then ЦенаГрп*(1-КлнНаВид/100)
			when КлнНаГрп<>0 then ЦенаГрп*(1-КлнНаГрп/100)
			else ЦенаГрп
		end
	else
		case
			when КлнНаТов<>0 then ЦенаКат*(1-КлнНаТов/100)
			when КлнНаВид<>0 then ЦенаКат*(1-КлнНаВид/100)
