              [ЗатратаНазвание3] [ЗатратаНазвание],
              [ЗатратаПлан3] [ЗатратыПлан],
              [ЗатратаФакт3] [ЗатратыФакт]
       FROM [dbo].[list_mlst]
       UNION ALL
       SELECT 4 [Затрата],
              [НомерМЛ],
              [АвтокодМЛ],
              [Дата],
              [Перевозчик],
              [Авто],
              [Водитель],
              [СтатусМЛ],
              [Маршрут],
              [Экспедитор],
              [ДатаВыезда],
              [ДатаРасч],
              [ДатаВыездаРасч],
              [ЗатратаНазвание4] [ЗатратаНазвание],
              [ЗатратаПлан4] [ЗатратыПлан],
              [ЗатратаФакт4] [ЗатратыФакт]
       FROM [dbo].[list_mlst]
       UNION ALL
       SELECT 5 [Затрата],
              [НомерМЛ],
              [АвтокодМЛ],
              [Дата],
              [Перевозчик],
              [Авто],
              [Водитель],
              [СтатусМЛ],
              [Маршрут],
              [Экспедитор],
