     zkz_nkl.ФлгОпер AND zkz_nkl.Накл = hs.ОпНакл
     JOIN LIST_SKL AS ls ON ls.ОпСкл = zkz_nkl.Скл AND ls.ОпТип =
     zkz_nkl.ФлгОпер AND zkz_nkl.Накл = ls.ОпНакл
